mapper.select:
		SELECT * 
		FROM(
			SELECT ROWNUM RN, "bseq", "boardnum", "title", "nickname",  "content", "filename", "writedate", "like_num", "unlike_num", "cnt", "UserId" 
			FROM(
				SELECT * 
				FROM "reviewboard" 
				WHERE "boardnum" = #{boardnum}
		
			AND "title" LIKE '%'||#{searchKeyword}||'%'
		
		
			AND "content" LIKE '%'||#{searchKeyword}||'%'
		
		ORDER BY "bseq" DESC
		) 
			
    		WHERE ROWNUM <= #{pageNum} * #{amount}
    		
    	) 
  		
  		WHERE RN > (#{pageNum} - 1) * #{amount}

		SELECT * 
		FROM(
			SELECT ROWNUM RN, "bseq", "boardnum", "title", "nickname",  "content", "filename", "writedate", "like_num", "unlike_num", "cnt", "UserId"
			FROM(
				SELECT * 
				FROM REVIEWBOARD 
				WHERE 1=1
				AND TITLE LIKE '%'||#{searchKeyword}||'%'
		ORDER BY BSEQ DESC
		) 
			
    		WHERE ROWNUM <= #{pageNum} * #{amount}
    		
    	) 
  		
  		WHERE RN > (#{pageNum} - 1) * #{amount}

SELECT count(*) FROM "reviewboard" WHERE "boardnum"=#{boardnum}
AND "title" LIKE '%'||#{searchKeyword}||'%'
AND "content" LIKE '%'||#{searchKeyword}||'%'

		
		SELECT "bseq", "title", "nickname", "content","filename","like_num", "unlike_num", "cnt"
		FROM (
			SELECT "bseq", "title", "nickname", "content","filename","like_num", "unlike_num", "cnt"
			FROM "reviewboard" 
    		WHERE ROWNUM <= #{pageNum} * #{amount} 
    		ORDER BY "bseq" DESC 
    	) 
  		WHERE RN > (#{pageNum} - 1) * #{amount}

		SELECT "bseq", "title", "nickname", "content","filename","like_num", "unlike_num", "cnt"
		FROM (
			SELECT ROWNUM RN,"bseq", "title", "nickname", "content","filename","like_num", "unlike_num", "cnt"
			FROM (
				SELECT * 
				FROM "reviewboard"  
				WHERE "boardnum"=#{boardnum} 
				
					AND "title" LIKE '%'||#{searchKeyword}||'%'
				
				
					AND "content" LIKE '%'||#{searchKeyword}||'%'
				
				ORDER BY "bseq" DESC
			) 
			
    		WHERE ROWNUM <= #{pageNum} * #{amount}
    		
    	) 
  		
  		WHERE RN > (#{pageNum} - 1) * #{amount}
