mapper.insert:
INSERT INTO REVIEWBOARD(BSEQ,BOARDNUM,TITLE,NICKNAME,CONTENT,FILENAME,LIKE_NUM,UNLIKE_NUM,CNT,USERID) 
VALUES ((select nvl(max(bseq), 0) + 1 from reviewboard), #{boardnum}, #{title},#{nickname}, #{content}, #{filename}
, #{like_num}, #{unlike_num}, #{cnt}, #{userId})

mapper.update:

	UPDATE REVIEWBOARD SET
		TITLE = #{title}, 
		CONTENT = #{content} 
	WHERE BSEQ = #{bseq}

mapper.select:
		SELECT * 
		FROM(
			SELECT ROWNUM RN, BSEQ, BOARDNUM, TITLE, NICKNAME, CONTENT,FILENAME,WRITEDATE, LIKE_NUM, UNLIKE_NUM, CNT, USERID 
			FROM(
				SELECT * 
				FROM "reviewboard" 
				WHERE boardnum = #{boardnum}
		
			AND TITLE LIKE '%'||#{searchKeyword}||'%'
		
		
			AND CONTENT LIKE '%'||#{searchKeyword}||'%'
		
		ORDER BY BSEQ DESC
		) 
			
    		WHERE ROWNUM <= #{pageNum} * #{amount}
    		
    	) 
  		
  		WHERE RN > (#{pageNum} - 1) * #{amount}

		SELECT * 
		FROM(
			SELECT ROWNUM RN, BSEQ, BOARDNUM, TITLE, NICKNAME, CONTENT,FILENAME,WRITEDATE, LIKE_NUM, UNLIKE_NUM, CNT, USERID 
			FROM(
				SELECT * 
				FROM REVIEWBOARD 
				WHERE 1=1
				AND TITLE LIKE '%'||#{searchKeyword}||'%'
		ORDER BY BSEQ DESC
		) 
			
    		WHERE ROWNUM <= #{pageNum} * #{amount}
    		
    	) 
  		
  		WHERE RN > (#{pageNum} - 1) * #{amount}

SELECT count(*) FROM REVIEWBOARD WHERE boardnum=#{boardnum}
AND TITLE LIKE '%'||#{searchKeyword}||'%'
AND CONTENT LIKE '%'||#{searchKeyword}||'%'

		
		SELECT BSEQ, TITLE, NICKNAME, CONTENT,FILENAME,LIKE_NUM, UNLIKE_NUM, CNT
		FROM (
			SELECT ROWNUM RN, BSEQ, TITLE, NICKNAME, CONTENT,LIKE_NUM, UNLIKE_NUM, FILENAME, CNT  
			FROM REVIEWBOARD 
    		WHERE ROWNUM <= #{pageNum} * #{amount} 
    		ORDER BY BSEQ DESC 
    	) 
  		WHERE RN > (#{pageNum} - 1) * #{amount}

SELECT BSEQ, TITLE, NICKNAME, CONTENT,FILENAME,LIKE_NUM, UNLIKE_NUM, CNT FROM ( SELECT ROWNUM RN, BSEQ, TITLE, NICKNAME, CONTENT,LIKE_NUM, UNLIKE_NUM, FILENAME, CNT FROM ( SELECT * FROM REVIEWBOARD WHERE boardnum = 1
AND TITLE LIKE '%'||#{searchKeyword}||'%'
AND CONTENT LIKE '%'||#{searchKeyword}||'%'
ORDER BY BSEQ DESC )
WHERE ROWNUM <= #{pageNum} * #{amount}
)
WHERE RN > (#{pageNum} - 1) * #{amount}
